    return texture2D(noiseTex, rd.xy).rgb;
}

vec3 pal (float t) {
    //return palette(t, vec3(cc01), vec3(cc02), vec3(cc03), vec3(cc04,cc05,cc06));
    return palette(t, vec3(.4), vec3(.6), vec3(.7), vec3(0,.1,.2));
}

vec4 tr(vec3 ro, vec3 rd, vec2 uv) {
    float td = .1;
    vec2 h;

    vec4 c = vec4(bg(rd, uv) * pal(rd.y + .6), 0);
    vec4 g = vec4(0);

    int bnc = 0;
    float en = 1.;

    for (int i = 0; i < 200; i++) {
        vec3 ip = ro + rd * td;
        h = map(ip);
        td += h.x * 0.15;

        if (h.y == 0.) {
            g += vec4(pal(icoDepth * 5.5 * .7) * exp(-h.x * 40.) * .1, .1);
        }
        if (h.y == 1.) {
            g += vec4(pal(icoDepth * 5.5 * .7) * exp(-h.x * 40.) * .02, .1);
        }

        if (h.x < 0.01) {
            vec3 inorm = norm(ip);

            float lite = dirlight(inorm, light);
            float sp =  spec(ip, inorm, light, 50.);

            if (h.y == 0.) {
                vec3 color = pal(icoDepth * 5.5 + .7);

                color = color;

                c = vec4(color,1.)  * lite * en;
                break;
            }

            if (h.y == 1.) {
                c = vec4( 1.);
                break;
            }

            if (h.y == 2.) { 
                //c = vec4(0,0,0,.01) * en;
                c += vec4(.3) * lite * en + sp * .6;
                ro = ip;
                rd = reflect(rd, inorm) + rnd(ip.y + ip.z + ip.x) * 0.04;
                td = .1;
                bnc += 1; 
                en = max(en - .1, 0.);
            }

            if (h.y == 3.) { 
                c += vec4(.1)* lite * en;
                ro = ip;
                rd = reflect(rd, inorm);
                td = .1;
                bnc += 1; 
                en = max(en - .3, 0.);
            }

            if (h.y == 4.) { 

                c = vec4(1.);
                break;
            }

            if (h.y == 5.) {
                c = vec4(greetsCol, 1.);
                break;
            }
        }

        if (c.a >= 1. || bnc > 3 || en < 0. || td > 1000.) break;
    }

    float ex =  exp(-td * 0.01);
     return vec4((vec3(c) + vec3(g)) * ex + (1.-ex) * bg(rd,uv)* pal(rd.y + .6), td/1.);
}

void main() {
    vec2 uv = texCoord;
    uv -= 0.5;
    uv /= vec2(resolution.y / resolution.x, 1);

