float sdFbm( vec3 p, float d, float th ){
   float s = 1.0;
   float mat = 0.;
   for( int i=0; i<11; i++ )
   {
       // evaluate new octave 
       float n = s*sdBase(p);
	
       // add
       n = smax(n,d-0.1*s,0.2*rnd(-s * 100.)*s);
       d = smin(n,d      ,0.4*s);
	
       // prepare next octave
       p = mat3( 0.00, 1.60, 1.20,
                -1.60, 0.72,-0.96,
