float pointlight(vec3 p,vec3 n,vec3 lightPos,vec3 camPos,float lightLen,vec3 attVec, float specPow, float specAmount) {
    vec3 lightDir = lightPos - p;
    float diffuse = max(0., dot(n, normalize(lightDir)));
    float d = distance(lightPos, p);
    vec3 k = attVec;
    float attenuation = 1. / (k.x + (k.y * d) + (k.z * d * d));
    vec3 ref = reflect(-normalize(lightDir), n);
    float specular = max(0., dot(ref, normalize(camPos - n)));
    specular = pow(specular, specPow);
    diffuse *= attenuation;
    specular *= attenuation;
    return diffuse + (specular * specAmount);
}
