// water
vec2 sdWater(vec3 p) { 
    float d = sdPlane(p, vec3(0,1,0), 4.) 
    + texture2D(waterNoiseTex, p.xz / 10. + sin(p.x * 0.1)  + cos(p.x * 0.2)).r * 0.1;

    
    return vec2(d, 0.);
}
 
vec2 map(vec3 p) {
    float m = 999.; 
    float mm = 0.; 
    
    vec2 cave = sdCave(p);
    if (cave.x < m) {m = cave.x; mm = cave.y;}
    
    vec2 abstract = sdAbstract(p);
    if (abstract.x < m) {m = abstract.x; mm = abstract.y + 1.;};
    
    vec2 water = sdWater(p);
    if (water.x < m) {m = water.x; mm = water.y + 2.;};
    
    vec2 lights = sdLights(p);
    if (lights.x < m) {m = lights.x; mm = lights.y + 3.;}
    
    return vec2(m, mm);
}

vec3 norm(vec3 p) {
    float E = 0.01; vec2 k = vec2(1, -1);
    
    return normalize(
      k.xyy * map(p + k.xyy * E).x +
      k.yyx * map(p + k.yyx * E).x +
      k.yxy * map(p + k.yxy * E).x +
      k.xxx * map(p + k.xxx * E).x 
    );
}
