float softshadow( in vec3 ro, in vec3 rd, float mint, float maxt, float k ){
    float res = 1.0;
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t).x;
        if( h<0.001 )
            return 0.0;
        res = min( res, k*h/t );
        t += h;
    }
    return res;
}

vec4 tr(vec3 ro, vec3 rd, vec2 uv) {
    float td = .1;
    vec2 h;
    
    vec4 c = vec4(0);
    vec4 g = vec4(0);
    
    int bnc = 0;
    float en = 1.;
     
    for (int i = 0; i < 300; i++) {
        vec3 ip = ro + rd * td;
        h = map(ip);
        td += h.x * 0.5; 
        
        // cube glow
        if (h.y == 1.) {
            g += vec4(vec3(pal(absSize * 20.)) * exp(-h.x * 2.) * smoothstep(.1,.4,absSize * 3.) * 0.1, .1);
        }
        
        // lights glow
        if (h.y == 3.) {
            g += vec4(vec3(1.) * exp(-h.x * 2.) * .1 * light1Str * 1., .1) ;
        }
         if (h.y == 4.) {
            g += vec4(vec3(1.) * exp(-h.x * 2.) * .1 * light2Str * 1., .1) ;
        }
         if (h.y == 5.) {
            g += vec4(vec3(1.) * exp(-h.x * 2.) * .1 * light3Str * 1., .1) ;
        }
         if (h.y == 6.) {
            g += vec4(vec3(1.) * exp(-h.x * 2.) * .1 * light4Str * 1., .1) ;
        }
        
        
        
        if (h.x < 0.01) { 
            vec3 inorm = norm(ip);
            
            float pLight1 = pointlight(
                    ip,
                    inorm,
                    light1Pos,
                    camPos,
                    1.,
                    vec3(0.002, 0.005, .004), 
                    10.,
                    .4
                );
                float pLight2 = pointlight(
                    ip,
                    inorm,
                    light2Pos,
                    camPos,
                    1.,
                    vec3(0.002, 0.005, .004), 
                    10.,
                    .4
                );
                float pLight3 = pointlight(
                    ip,
                    inorm,
                    light3Pos,
                    camPos,
                    1.,
                    vec3(0.002, 0.005, .004), 
                    10.,
                    .4
                );
                float pLight4 = pointlight(
                    ip,
                    inorm,
                    light4Pos,
                    camPos,
                    1.,
                    vec3(0.002, 0.005, .004), 
                    10.,
                    .4
                );
            
            // cave
            if (h.y == 0.) {
                float occ = ao(ip, inorm, .03); 
                
                vec3 albedo = pal(length(ip) / 1.9 + 0.15);
                vec3 normalAlb = pal(inorm.x + inorm.y * 1. + inorm.z);
                albedo = albedo * 0.3 + normalAlb * 0.2;
                
                c += vec4(
                    ((albedo * pLight1 * light1Str * light1Color) 
                    + (albedo * pLight2 * light2Str * light2Color) 
                    + (albedo * pLight3 * light3Str * light3Color) 
                    + (albedo * pLight4 * light4Str * light4Color))
                    * occ
                    , 1.
                ) * en;
                break;
            }
            
            // cube
            else if (h.y == 1.) {
                //float occ = ao(ip, inorm, .03);
                
                //float n = biplanar(noiseTex, ip, inorm, .1).r;
                vec3 albedo = pal(absSize * 20.) * 0.7;
                c += vec4(
                    (albedo * pLight1 * light1Str * light1Color) 
                    + (albedo * pLight2 * light2Str * light2Color) 
                    + (albedo * pLight3 * light3Str * light3Color) 
                    + (albedo * pLight4 * light4Str * light4Color)
                    , .6
                ) * en;
                //c += vec4( * pLight, .1) * en;
                ro = ip;
                rd = reflect(rd, inorm);
                td = .1;
                bnc += 1; 
                en = max(en - .6, 0.);
            }   
            
            // water
            else if (h.y == 2.) {
                c += vec4(.0) * en;
                ro = ip;
                rd = reflect(rd, inorm);
                td = .1;
                bnc += 1; 
                en = max(en - .8, 0.);
            } 
            
            // lights
            else if (h.y == 3.) {
                c = vec4(light1Color + vec3(.3), 1.) * (light1Str * 2.3 + 0.1);
                return c;
            } 
            else if (h.y == 4.) {
                c = vec4(light2Color + vec3(.3), 1.) * (light2Str * 2.3 + 0.1);
                return c;
            }
            else if (h.y == 5.) {
                c = vec4(light3Color + vec3(.3), 1.) * (light3Str * 2.3 + 0.1);
                return c;
            }
            else if (h.y == 6.) {
                c = vec4(light4Color + vec3(.3), 1.)* (light4Str * 2.3 + 0.1);
                return c;
            }
        }
        
        if (c.a >= 1. || bnc > 1 || en < 0. || td > 100.) break;
    }
    
    return vec4((vec3(c) + vec3(g)) * exp(-td * 0.05), td/1000.);
}

